INSERT INTO `web_news` VALUES ('61', 'Cam kết chất lượng đào tạo năm 2010 - 2011', '', '<table border=\"1\" cellpadding=\"0\" cellspacing=\"0\" style=\"border-collapse:collapse; color:rgb(0, 0, 0); font-family:tahoma,helvetica,sans-serif; font-size:12px; font-stretch:normal; height:1320px; line-height:normal; margin:0px; outline:none; padding:0px; text-align:justify; width:758px\">\r\n	<tbody>\r\n		<tr>\r\n			<td rowspan=\"2\">\r\n			<p><strong>Stt</strong></p>\r\n			</td>\r\n			<td rowspan=\"2\">\r\n			<p><strong>Nội dung</strong></p>\r\n			</td>\r\n			<td colspan=\"4\">\r\n			<p><strong>Chia theo c&aacute;c tr&igrave;nh độ đ&agrave;o tạo v&agrave; hệ đ&agrave;o tạo</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Cao đẳng</strong></p>\r\n\r\n			<p><strong>ch&iacute;nh qui</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>Trung cấp</strong></p>\r\n\r\n			<p><strong>chuy&ecirc;n nghiệp</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>Cao đẳng</strong></p>\r\n\r\n			<p><strong>vừa học vừa l&agrave;m</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>Đ&agrave;o tạo li&ecirc;n th&ocirc;ng từ TCCN l&ecirc;n CĐ</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>1</p>\r\n			</td>\r\n			<td>\r\n			<p>Điều kiện tuyển sinh</p>\r\n			</td>\r\n			<td>\r\n			<p>- Đ&atilde; tốt nghiệp THPT hoặc tương đương trở l&ecirc;n</p>\r\n\r\n			<p>- H&igrave;nh thức tuyển sinh:</p>\r\n\r\n			<p>+ C&aacute;c ng&agrave;nh kh&ocirc;ng c&oacute; năng khiếu (khối A, B, C, D1) x&eacute;t tuyển theo kết quả thi 3 chung của Bộ GD&amp;ĐT .</p>\r\n\r\n			<p>+ C&aacute;c ng&agrave;nh c&oacute; năng khiếu (khối M, N, H) thi tuyển 3 m&ocirc;n theo Lịch&nbsp; thi của Bộ GD&amp;ĐT.</p>\r\n			</td>\r\n			<td>\r\n			<p>- Đ&atilde; học hết THPT (hoặc tương đương), thời gian đ&agrave;o tạo 2 năm; học hết THCS, thời gian đ&agrave;o tạo 3 năm.</p>\r\n\r\n			<p>- H&igrave;nh thức tuyển sinh:</p>\r\n\r\n			<p>+ C&aacute;c ng&agrave;nh kh&ocirc;ng c&oacute; năng khiếu x&eacute;t tuyển theo kết quả học tập lớp cuối cấp.</p>\r\n\r\n			<p>+ C&aacute;c ng&agrave;nh c&oacute; năng khiếu, x&eacute;t tuyển m&ocirc;n văn lớp 12 v&agrave; thi m&ocirc;n năng khiếu.</p>\r\n			</td>\r\n			<td>\r\n			<p>- Đ&atilde; tốt nghiệp THPT hoặc tương đương trở l&ecirc;n</p>\r\n\r\n			<p>- Thi tuyển 3 m&ocirc;n (theo khối thi), thực hiện thi theo kế hoạch của Bộ GD&amp;ĐT</p>\r\n			</td>\r\n			<td>\r\n			<p>- Đ&atilde; tốt nghiệp TCCN trở l&ecirc;n</p>\r\n\r\n			<p>- Thi tuyển 3 m&ocirc;n (theo khối thi), thực hiện theo kế hoạch của Bộ GD&amp;ĐT</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>2</p>\r\n			</td>\r\n			<td>\r\n			<p>Chương tr&igrave;nh đ&agrave;o tạo c&aacute;c ng&agrave;nh</p>\r\n			</td>\r\n			<td colspan=\"4\">\r\n			<p>Chương tr&igrave;nh ban h&agrave;nh k&egrave;m theo c&aacute;c Quyết định số 827/QĐ-CĐSPTW ng&agrave;y 18/12/2007 v&agrave; số 453/QĐ-CĐSPTW ng&agrave;y 20/5/2009 về việc ban h&agrave;nh Chương tr&igrave;nh gi&aacute;o dục tr&igrave;nh độ cao đẳng, số 869 QĐ-CĐSPTW ng&agrave;y 28/12/2007 về việc ban h&agrave;nh Chương tr&igrave;nh gi&aacute;o dục TCCN của Hiệu trưởng Trường Cao đẳng Sư phạm Trung ương tr&ecirc;n cơ sở Chương tr&igrave;nh khung của Bộ GD&amp;ĐT, c&oacute; tham khảo chương tr&igrave;nh của c&aacute;c nước ti&ecirc;n tiến. Bao gồm c&aacute;c chương tr&igrave;nh:</p>\r\n\r\n			<p>- Tr&igrave;nh độ cao đẳng (16 ng&agrave;nh): Gi&aacute;o dục Mầm non, Sư phạm &acirc;m nhạc, Sư phạm Mỹ thuật, Gi&aacute;o dục Đặc biệt, Sư phạm C&ocirc;ng nghệ, Sư phạm Gi&aacute;o dục c&ocirc;ng d&acirc;n, C&ocirc;ng t&aacute;c x&atilde; hội, Tin học, Th&ocirc;ng tin &ndash; Thư viện, Quản trị văn ph&ograve;ng, Thư k&iacute; văn ph&ograve;ng, Thiết kế thời trang, Quản l&iacute; văn h&oacute;a, H&agrave;nh ch&iacute;nh văn thư (gh&eacute;p với Lưu trữ học), Việt Nam học (chuy&ecirc;n ng&agrave;nh Văn h&oacute;a du lịch), C&ocirc;ng nghệ Thiết bị trường học.</p>\r\n\r\n			<p>- Tr&igrave;nh độ TCCN (10 ng&agrave;nh): Gi&aacute;o dục Mầm non, Sư phạm &acirc;m nhạc, Sư phạm Mỹ thuật, Tin học, Thư viện, Thư viện &ndash; Thiết bị trường học, H&agrave;nh ch&iacute;nh văn thư, bảo hiểm, C&ocirc;ng nghệ may v&agrave; Thiết kế thời trang, Nghiệp vụ lữ h&agrave;nh v&agrave; Hướng dẫn du lịch.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>3</p>\r\n			</td>\r\n			<td>\r\n			<p>Y&ecirc;u cầu về th&aacute;i độ học tập của người học</p>\r\n			</td>\r\n			<td>\r\n			<p>- Thực hiện Qui chế 25 về đ&agrave;o tạo đại học, cao đẳng.</p>\r\n\r\n			<p>- Sinh vi&ecirc;n c&oacute; thể dự giờ l&ecirc;n lớp hoặc tự nghi&ecirc;n cứu đối với c&aacute;c tiết l&iacute; thuyết.</p>\r\n\r\n			<p>- Sinh vi&ecirc;n phải dự đầy đủ c&aacute;c tiết thực h&agrave;nh.</p>\r\n			</td>\r\n			<td>\r\n			<p>- Thực hiện Qui chế 40 về đ&agrave;o tạo TCCN hệ ch&iacute;nh qui.</p>\r\n\r\n			<p>- Học sinh phải dự đầy đủ c&aacute;c tiết l&ecirc;n lớp (kể cả c&aacute;c tiết l&iacute; thuyết v&agrave; thực h&agrave;nh)</p>\r\n			</td>\r\n			<td>\r\n			<p>- Thực hiện Qui chế 36 về đ&agrave;o tạo đại học, cao đẳng h&igrave;nh thức vừa l&agrave;m vừa học.</p>\r\n\r\n			<p>-Sinh vi&ecirc;n phải dự đầy đủ c&aacute;c tiết l&ecirc;n lớp (kể cả c&aacute;c tiết l&iacute; thuyết v&agrave; thực h&agrave;nh)</p>\r\n			</td>\r\n			<td>\r\n			<p>C&oacute; 2 h&igrave;nh thức</p>\r\n\r\n			<p>- Đ&agrave;o tạo theo h&igrave;nh thức ch&iacute;nh qui: Qui chế 25.</p>\r\n\r\n			<p>- Đ&agrave;o tạo h&igrave;nh thức vừa l&agrave;m vừa học: Qui chế 36.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>4</p>\r\n			</td>\r\n			<td>\r\n			<p>C&aacute;c hoạt động hỗ trợ học tập, sinh hoạt cho người học</p>\r\n			</td>\r\n			<td colspan=\"4\">\r\n			<p>- Thực hiện đầy đủ c&aacute;c qui chế về học sinh, sinh vi&ecirc;n hiện h&agrave;nh của Bộ GD&amp;ĐT.</p>\r\n\r\n			<p>- Thực hiện đầy đủ c&aacute;c ch&iacute;nh s&aacute;ch đối với người học theo c&aacute;c chế độ v&agrave; qui định của Nh&agrave; nước.</p>\r\n\r\n			<p>- Tư vấn cho người học về việc học tập, r&egrave;n luyện.</p>\r\n\r\n			<p>- Người học thuộc chế độ ch&iacute;nh s&aacute;ch c&oacute; chỗ ở trong khu K&yacute; t&uacute;c x&aacute; của Trường.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>5</p>\r\n			</td>\r\n			<td>\r\n			<p>Điều kiện cơ sở vật chất của trường</p>\r\n			</td>\r\n			<td colspan=\"4\">\r\n			<p>- Ph&ograve;ng học đảm bảo đ&uacute;ng qui định, c&oacute; đầy đủ trang thiết bị.</p>\r\n\r\n			<p>- C&oacute; đủ c&aacute;c ph&ograve;ng th&iacute; nghiệm, thực h&agrave;nh v&agrave; cơ sở thực h&agrave;nh nghề nghiệp.</p>\r\n\r\n			<p>- Thư viện đ&aacute;p ứng đầy đủ t&agrave;i liệu học tập v&agrave; hỗ trợ th&ocirc;ng tin đ&agrave;o tạo.</p>\r\n\r\n			<p>- Đảm bảo cơ sở vật chất tối thiểu để tổ chức c&aacute;c họat động ngoại kh&oacute;a.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>6</p>\r\n			</td>\r\n			<td>\r\n			<p>Đội ngũ giảng vi&ecirc;n v&agrave; c&aacute;n bộ quản l&iacute;</p>\r\n			</td>\r\n			<td colspan=\"4\">\r\n			<p>- Đội ngũ giảng vi&ecirc;n v&agrave; c&aacute;n bộ quản l&iacute;: c&oacute; đủ số lượng theo y&ecirc;u cầu ng&agrave;nh học, 100% đạt chuẩn trở l&ecirc;n, trong đ&oacute; c&oacute; 60% tr&ecirc;n chuẩn.</p>\r\n\r\n			<p>- C&ocirc;ng t&aacute;c quản l&iacute; thực hiện đầy đủ v&agrave; đ&uacute;ng theo c&aacute;c qui định hiện h&agrave;nh của Nh&agrave; nước.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>7</p>\r\n			</td>\r\n			<td>\r\n			<p>Mục ti&ecirc;u, kiến thức,</p>\r\n\r\n			<p>kĩ năng</p>\r\n			</td>\r\n			<td colspan=\"4\">\r\n			<p>- Đảm bảo theo Chuẩn đầu ra ban h&agrave;nh k&egrave;m theo Quyết định số 404/QĐ-CĐSPTW ng&agrave;y 22/4/2010 của Hiệu trưởng Trường Cao đẳng Sư phạm Trung ương.</p>\r\n\r\n			<p>- Người học biết ngoại ngữ tiếng Anh theo y&ecirc;u cầu của tr&igrave;nh độ đ&agrave;o tạo.</p>\r\n\r\n			<p>- Người học sau khi tốt nghiệp sử dụng th&agrave;nh thạo c&aacute;c phần mềm tin học văn ph&ograve;ng v&agrave; phần mềm li&ecirc;n quan đến chuy&ecirc;n ng&agrave;nh đ&agrave;o tạo.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>8</p>\r\n			</td>\r\n			<td>\r\n			<p>Vị tr&iacute; l&agrave;m việc sau khi tốt nghiệp</p>\r\n			</td>\r\n			<td colspan=\"4\">\r\n			<p>Người học đủ tr&igrave;nh độ xin được việc l&agrave;m đ&uacute;ng theo ng&agrave;nh đ&agrave;o tạo</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '0', '302', 'Báo cáo Ba công khai', null, null, '1', '0', '302', '0', '1', null);
INSERT INTO `web_news` VALUES ('62', 'CHƯƠNG TRÌNH HÀNH ĐỘNG ĐỔI MỚI QUẢN LÝ, NĂNG CAO CHẤT LƯỢNG ĐÀO TẠO GIAI ĐOẠN 2010 - 2012', '', '<p style=\"text-align: center;\"><strong>HIỆU TRƯỞNG TRƯỜNG CAO ĐẲNG SƯ PHẠM TRUNG ƯƠNG</strong></p>\r\n\r\n<p style=\"text-align:justify\">Căn cứ Nghị định số 93/HĐBT ng&agrave;y 28 th&aacute;ng 5 năm 1988 của Hội đồng Bộ trưởng (nay l&agrave; Ch&iacute;nh phủ) về việc th&agrave;nh lập Trường cao đẳng sư phạm Nh&agrave; trẻ - Mẫu gi&aacute;o TW 1;</p>\r\n\r\n<p style=\"text-align:justify\">Căn cứ Quyết định số 509/QĐ-BGDĐT ng&agrave;y 26 th&aacute;ng 1 năm 2006 của Bộ trưởng Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo về việc đổi t&ecirc;n Trường cao đẳng sư phạm Nh&agrave; trẻ - Mẫu gi&aacute;o TW1 th&agrave;nh Trường Cao đẳng Sư phạm Trung ương;</p>\r\n\r\n<p style=\"text-align:justify\">Căn cứ Quyết định số 622 QĐ/TCCB ng&agrave;y 12/7/1985 của Bộ ĐH-THCN (nay l&agrave; Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo) về qui định tr&aacute;ch nhiệm, quyền hạn của Hiệu trưởng c&aacute;c Trường trực thuộc Bộ;</p>\r\n\r\n<p style=\"text-align:justify\">Căn cứ Điều lệ Trường cao đẳng ban h&agrave;nh k&egrave;m theo Th&ocirc;ng tư số 14/2009/TT-BGDĐT ng&agrave;y 28/5/2009 của Bộ trưởng Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo;</p>\r\n\r\n<p style=\"text-align:justify\">Theo Bi&ecirc;n bản Hội nghị thảo luận, qu&aacute;n triệt Chỉ thị của Thủ tướng Ch&iacute;nh phủ v&agrave; Nghị quyết 05 của Ban c&aacute;n sự Đảng bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo về đổi mới quản l&yacute; gi&aacute;o dục đại học giai đoạn 2010-2012 của Trường Cao đẳng sư phạm Trung ương,</p>\r\n\r\n<p><strong>QUYẾT ĐỊNH:</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>Điều 1.</strong>&nbsp;Ph&ecirc; duyệt k&egrave;m theo Quyết định n&agrave;y Chương tr&igrave;nh h&agrave;nh động triển khai thực hiện Chỉ thị của Thủ tướng Ch&iacute;nh phủ v&agrave; Nghị quyết 05 của Ban c&aacute;n sự Đảng bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo về đổi mới quản l&yacute; gi&aacute;o dục đại học giai đoạn 2010-2012 của Trường Cao đẳng Sư phạm Trung ương<strong><em>.</em></strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>Điều 2.</strong>&nbsp;Quyết định n&agrave;y c&oacute; hiệu lực kể từ ng&agrave;y k&yacute;.</p>\r\n\r\n<p style=\"text-align:justify\">C&aacute;c &ocirc;ng, b&agrave; Trưởng c&aacute;c đơn vi v&agrave; to&agrave;n thể c&aacute;n bộ, giảng vi&ecirc;n trong Trường Cao đẳng Sư phạm Trung ương chịu tr&aacute;ch nhiệm thi h&agrave;nh Quyết định n&agrave;y./.</p>\r\n\r\n<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" style=\"border-collapse:collapse; color:rgb(0, 0, 0); font-family:tahoma,helvetica,sans-serif; font-size:12px; font-stretch:normal; line-height:normal; margin:0px; outline:none; padding:0px; text-align:justify; width:720px\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td colspan=\"2\">\r\n			<p><strong><em>&nbsp;</em></strong></p>\r\n			</td>\r\n			<td colspan=\"2\">\r\n			<p style=\"text-align: center;\"><strong>HIỆU TRƯỞNG</strong></p>\r\n\r\n			<p style=\"text-align: center;\">(Đ&atilde; k&iacute;)</p>\r\n\r\n			<p style=\"text-align: center;\"><strong>PGS.TS. Nguyễn Văn L&ecirc;</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td colspan=\"2\">\r\n			<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</strong></p>\r\n			</td>\r\n			<td>\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n			<td>&nbsp;</td>\r\n			<td>&nbsp;</td>\r\n			<td>&nbsp;</td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;(K&egrave;m theo Quyết định số 405 /QĐ-CĐSPTW ng&agrave;y 22 th&aacute;ng 4 năm 2010</p>\r\n\r\n<p>của Hiệu trưởng Trường Cao đẳng Sư phạm Trung ương)</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong>1. Sứ mệnh, tầm nh&igrave;n v&agrave; nhiệm vụ ch&iacute;nh của Nh&agrave; trường giai đoạn 2010-2012 v&agrave; giai đoạn 2010-2020</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong><em>1.1. Sứ mệnh</em></strong>: Trường Cao đẳng Sư phạm Trung ương phấn đấu trở th&agrave;nh cơ sở đ&agrave;o tạo đa ng&agrave;nh, đa lĩnh vực nhằm đ&agrave;o tạo đội ngũ gi&aacute;o vi&ecirc;n v&agrave; c&aacute;n bộ c&oacute; phẩm chất v&agrave; năng lực đ&aacute;p ứng nhu cầu đa dạng về nguồn nh&acirc;n lực phục vụ sự nghiệp c&ocirc;ng nghiệp h&oacute;a, hiện đại h&oacute;a đất nước.</p>\r\n\r\n<p style=\"text-align:justify\"><strong><em>1.2.&nbsp;</em></strong><strong><em>Tầm nh&igrave;n</em></strong>: Đến cuối những năm 2020, Trường Cao đẳng Sư phạm Trung ương phấn đấu trở th&agrave;nh cơ sở gi&aacute;o dục v&agrave; đ&agrave;o tạo c&oacute; c&aacute;c chuy&ecirc;n ng&agrave;nh tr&igrave;nh độ cao đẳng đạt chuẩn trong nước, chuẩn của c&aacute;c nước trong khu vực v&agrave; tr&ecirc;n thế giới.</p>\r\n\r\n<p style=\"text-align:justify\"><strong><em>1.3. Nhiệm vụ ch&iacute;nh</em></strong><em>:</em></p>\r\n\r\n<ul>\r\n	<li>Đ&agrave;o tạo tr&igrave;nh độ cao đẳng v&agrave; li&ecirc;n kết đ&agrave;o tạo đại học đội ngũ c&aacute;n bộ v&agrave; gi&aacute;o vi&ecirc;n c&aacute;c cấp nhằm cung cấp nguồn nh&acirc;n lực th&iacute;ch ứng với m&ocirc;i trường l&agrave;m việc v&agrave; c&oacute; khả năng giải quyết những vấn đề nảy sinh trong thực tiễn.</li>\r\n	<li>Nghi&ecirc;n cứu khoa học gi&aacute;o dục, đặc biệt l&agrave; khoa học gi&aacute;o dục Mầm non v&agrave; c&aacute;c lĩnh vực đ&agrave;o tạo. Tham vấn cho c&aacute;c cấp l&atilde;nh đạo, c&aacute;c cơ quan ban ng&agrave;nh, địa phương về c&aacute;c vấn đề c&oacute; li&ecirc;n quan.</li>\r\n	<li>Bồi dưỡng kiến thức, kĩ năng, nghiệp vụ quản l&yacute;, nghiệp vụ sư phạm cho đội ngũ c&aacute;n bộ, gi&aacute;o vi&ecirc;n...về c&aacute;c lĩnh vực đ&agrave;o tạo.</li>\r\n	<li>Hợp t&aacute;c quốc tế về c&aacute;c lĩnh vực đ&agrave;o tạo v&agrave; nghi&ecirc;n cứu khoa học cũng như chuyển giao c&ocirc;ng nghệ.</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><strong>2. Những việc cần l&agrave;m dứt điểm trong năm 2010, 2011, 2012</strong></p>\r\n\r\n<p style=\"text-align:justify\">2.1. Ho&agrave;n thiện cơ cấu tổ chức Nh&agrave; trường.</p>\r\n\r\n<p style=\"text-align:justify\">2.2. X&acirc;y dựng chương tr&igrave;nh đ&agrave;o tạo ng&agrave;nh ti&ecirc;n tiến.</p>\r\n\r\n<p style=\"text-align:justify\">2.3. Ho&agrave;n thiện b&aacute;o c&aacute;o tự đ&aacute;nh gi&aacute;, ho&agrave;n thiện c&aacute;c qui định v&agrave; kiểm định chất lượng gi&aacute;o dục.</p>\r\n\r\n<p style=\"text-align:justify\">2.4. Ho&agrave;n th&agrave;nh lộ tr&igrave;nh chuyển đổi từ đ&agrave;o tạo theo học chế ni&ecirc;n chế sang đ&agrave;o tạo theo học chế t&iacute;n chỉ.</p>\r\n\r\n<p style=\"text-align:justify\">2.5. C&ocirc;ng bố chuẩn đầu ra, c&ocirc;ng bố đầy đủ c&aacute;c nội dung c&ocirc;ng khai của Nh&agrave; trường.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>3. C&aacute;c biện ph&aacute;p thực hiện</strong></p>\r\n\r\n<p style=\"text-align:justify\">3.1. Ho&agrave;n thiện c&aacute;c văn bản về c&ocirc;ng t&aacute;c quản l&yacute; đ&agrave;o tạo, c&ocirc;ng t&aacute;c sinh vi&ecirc;n, qui chế phối hợp hoạt động giữa c&aacute;c đơn vị nhằm tăng hiệu quả quản l&yacute; trong Nh&agrave; trường. Qui định cụ thể chức năng nhiệm vụ c&aacute;c đơn vị trong Trường để tăng cường t&iacute;nh tự chủ, tự chịu tr&aacute;ch nhiệm trong việc quản l&yacute; nguồn lực v&agrave; đảm bảo chất lượng gi&aacute;o dục.</p>\r\n\r\n<p style=\"text-align:justify\">3.2. X&aacute;c định qui m&ocirc; đ&agrave;o tạo, cơ cấu c&aacute;c ng&agrave;nh nghề v&agrave; loại h&igrave;nh đ&agrave;o tạo ph&ugrave; hợp với nhu cầu x&atilde; hội v&agrave; điều kiện thực tế của Trường. Đẩy mạnh đ&agrave;o tạo theo địa chỉ nhằm đ&aacute;p ứng nhu cầu x&atilde; hội.</p>\r\n\r\n<p style=\"text-align:justify\">3.3. Th&agrave;nh lập v&agrave; tăng cường hoạt động của bộ phận kiểm định v&agrave; đảm bảo chất lượng gi&aacute;o dục độc lập.</p>\r\n\r\n<p style=\"text-align:justify\">- X&acirc;y dựng ti&ecirc;u chuẩn đ&aacute;nh gi&aacute; giảng vi&ecirc;n v&agrave; thực hiện việc đ&aacute;nh gi&aacute; chất lượng giảng vi&ecirc;n h&agrave;ng năm.</p>\r\n\r\n<p style=\"text-align:justify\">- Ho&agrave;n thiện chuẩn đầu ra, coi đ&acirc;y l&agrave; một trong c&aacute;c hệ thống th&ocirc;ng số thể hiện sự gắn kết Nh&agrave; trường với nhu cầu đ&agrave;o tạo của x&atilde; hội, để đ&aacute;nh gi&aacute; kết quả học tập của người dạy v&agrave; người học.</p>\r\n\r\n<p style=\"text-align:justify\">- Thường xuy&ecirc;n tổ chức lấy &yacute; kiến phản hồi về Chương tr&igrave;nh đ&agrave;o tạo từ người học v&agrave; từ c&aacute;c nh&agrave; tuyển dụng.</p>\r\n\r\n<p style=\"text-align:justify\">- Đẩy mạnh hoạt động thanh kiểm tra hoạt động đ&agrave;o tạo trong Trường, đặc biệt l&agrave; kiểm tra chất lượng đ&agrave;o tạo theo h&igrave;nh thức vừa l&agrave;m vừa học, thanh tra c&ocirc;ng t&aacute;c giảng dạy tại c&aacute;c đơn vị li&ecirc;n kết đ&agrave;o tạo.</p>\r\n\r\n<p style=\"text-align:justify\">- Đăng k&iacute; đ&aacute;nh gi&aacute; ngo&agrave;i trong năm 2012.</p>\r\n\r\n<p style=\"text-align:justify\">3.4. Ho&agrave;n th&agrave;nh chuyển đổi chương tr&igrave;nh đ&agrave;o tạo theo học chế t&iacute;n chỉ:</p>\r\n\r\n<p style=\"text-align:justify\">- Chương tr&igrave;nh phải ph&ugrave; hợp với mục ti&ecirc;u đ&agrave;o tạo, đảm bảo chuẩn kiến thức cơ bản của m&ocirc;n học, tăng cường t&iacute;nh lựa chọn, t&iacute;nh tự học, tự nghi&ecirc;n cứu của sinh vi&ecirc;n theo hướng dẫn của giảng vi&ecirc;n.</p>\r\n\r\n<p style=\"text-align:justify\">- Nội dung kiến thức trong gi&aacute;o tr&igrave;nh phải đảm bảo c&acirc;n đối giữa l&iacute; luận v&agrave; thực h&agrave;nh, tiếp cận với chương tr&igrave;nh ti&ecirc;n tiến của c&aacute;c nước trong khu vực.</p>\r\n\r\n<p style=\"text-align:justify\">3.5. X&aacute;c định tr&aacute;ch nhiệm đối với giảng vi&ecirc;n, sinh vi&ecirc;n để n&acirc;ng cao chất lượng đ&agrave;o tạo, hiệu quả nghi&ecirc;n cứu khoa học, đồng bộ với c&aacute;c đổi mới quản l&iacute; nh&agrave; nước về gi&aacute;o dục đại học.</p>\r\n\r\n<p style=\"text-align:justify\">- Bồi dưỡng, n&acirc;ng cao năng lực đội ngũ c&aacute;n bộ quản l&iacute; c&aacute;c khoa, ph&ograve;ng v&agrave; c&aacute;c đơn vị trực thuộc trong Trường.</p>\r\n\r\n<p style=\"text-align:justify\">- X&acirc;y dựng đội ngũ giảng vi&ecirc;n c&oacute; tr&igrave;nh độ, kinh nghiệm v&agrave; năng lực chuy&ecirc;n m&ocirc;n, c&oacute; đạo đức nghề nghiệp&hellip;</p>\r\n\r\n<p style=\"text-align:justify\">- Đẩy mạnh hoạt động nghi&ecirc;n cứu khoa học, đặc biệt ch&uacute; trọng c&aacute;c đề t&agrave;i li&ecirc;n quan đến x&acirc;y dựng chương tr&igrave;nh ti&ecirc;n tiến, v&agrave; đa dạng h&oacute;a c&aacute;c phương ph&aacute;p giảng dạy ti&ecirc;n tiến, đổi mới phương ph&aacute;p học v&agrave; đổi mới phương ph&aacute;p đ&aacute;nh gi&aacute; kết quả học tập của người học.</p>\r\n\r\n<p style=\"text-align:justify\">3.6. Đầu tư cho việc n&acirc;ng cấp hạ tầng cơ sở phục vụ qu&aacute; tr&igrave;nh đ&agrave;o tạo:</p>\r\n\r\n<p style=\"text-align:justify\">- X&acirc;y dựng ph&ograve;ng học chất lượng cao phục vụ việc giảng dạy, đặc biệt l&agrave; c&aacute;c ph&ograve;ng học phục vụ giảng dạy v&agrave; học tập học phần tiếng Anh v&agrave; c&aacute;c học phần chuy&ecirc;n ng&agrave;nh.</p>\r\n\r\n<p style=\"text-align:justify\">- Củng cố c&aacute;c ph&ograve;ng thực h&agrave;nh, bổ sung trang thiết bị phục vụ giảng dạy v&agrave; học tập.</p>\r\n\r\n<p style=\"text-align:justify\">- X&acirc;y dựng thư viện điện tử; bổ sung gi&aacute;o tr&igrave;nh, s&aacute;ch tham khảo; n&acirc;ng cấp mạng internet, &hellip;</p>\r\n\r\n<p style=\"text-align:justify\">3.7. Chăm lo đời sống vật chất, tinh thần của c&aacute;n bộ, nh&acirc;n vi&ecirc;n tạo động lực cho sự ph&aacute;t triển.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>4. Tổ chức thực hiện</strong></p>\r\n\r\n<p style=\"text-align:justify\">4.1. Căn cứ v&agrave;o sự ph&acirc;n c&ocirc;ng tr&aacute;ch nhiệm của c&aacute;c c&aacute; nh&acirc;n v&agrave; c&aacute;c đơn vị trong Trường, h&agrave;ng th&aacute;ng h&agrave;ng qu&iacute; c&oacute; b&aacute;o c&aacute;o về kế hoạch, tiến độ v&agrave; kết quả thực hiện cụ thể trong c&aacute;c Hội nghị giao ban; tổng hợp b&aacute;o c&aacute;o kết quả thực hiện v&agrave; xin &yacute; kiến chỉ đạo của Đảng ủy Trường.</p>\r\n\r\n<p style=\"text-align:justify\">4.2. H&agrave;ng năm thực hiện việc tổng kết v&agrave; đ&aacute;nh gi&aacute; c&ocirc;ng khai nhằm ho&agrave;n thiện v&agrave; n&acirc;ng cao chất lượng đ&agrave;o tạo, hiệu quả c&ocirc;ng t&aacute;c quản l&yacute;.</p>\r\n\r\n<p style=\"text-align:justify\">4.3. Kết quả thực hiện h&agrave;ng năm được b&aacute;o c&aacute;o theo đ&uacute;ng c&aacute;c qui định của Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" style=\"border-collapse:collapse; color:rgb(0, 0, 0); font-family:tahoma,helvetica,sans-serif; font-size:12px; font-stretch:normal; line-height:normal; margin:0px; outline:none; padding:0px; text-align:justify; width:613px\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><strong><em>&nbsp;&nbsp; Nơi nhận:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</em></strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n			<p>&nbsp; - Như k&iacute;nh gửi;</p>\r\n\r\n			<p>&nbsp; - Lưu: VT, QLĐT.</p>\r\n			</td>\r\n			<td>\r\n			<p><strong>HIỆU TRƯỞNG</strong></p>\r\n\r\n			<p>(Đ&atilde; k&iacute;)</p>\r\n\r\n			<p><strong>PGS.TS. Nguyễn Văn L&ecirc;</strong></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '0', '302', 'Báo cáo Ba công khai', null, null, '1', '0', '302', '0', '1', null);
